import React, { useState, useEffect } from 'react';

function CryptoTable() {
  const [cryptoData, setCryptoData] = useState([]);
  const [searchText, setSearchText] = useState('');
  const [sortKey, setSortKey] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    const url = 'https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=10&page=1&sparkline=false';
    
    fetch(url)
      .then(response => response.json())
      .then(data => setCryptoData(data))
      .catch(error => console.error('Error fetching data:', error));
  };

  const handleSearch = (e) => {
    setSearchText(e.target.value.toLowerCase());
  };

  const handleSort = (key) => {
    setSortKey(key);
  };

  const filteredData = cryptoData.filter(item =>
    item.name.toLowerCase().includes(searchText) || item.symbol.toLowerCase().includes(searchText)
  );

  const sortedData = sortKey ?
    [...filteredData].sort((a, b) => {
      const aValue = parseFloat(a[key]);
      const bValue = parseFloat(b[key]);
      return key === 'market_cap' ? bValue - aValue : aValue - bValue;
    }) :
    filteredData;

  return (
    <div>
      <input type="text" value={searchText} onChange={handleSearch} placeholder="Search..."/>
      <button onClick={() => handleSort('market_cap')}>Sort by Market Cap</button>
      <button onClick={() => handleSort('price_change_percentage_24h')}>Sort by 24h Change</button>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Symbol</th>
            <th>Price (USD)</th>
            <th>Volume</th>
            <th>Market Cap</th>
            <th>24h Change (%)</th>
          </tr>
        </thead>
        <tbody>
          {sortedData.map(item => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.symbol}</td>
              <td>{item.current_price}</td>
              <td>{item.total_volume}</td>
              <td>{item.market_cap}</td>
              <td>{item.price_change_percentage_24h}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default CryptoTable;
